package com.example.cobaaan;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import java.util.ArrayList;public class AdapterRows extends RecyclerView.Adapter<AdapterRows.ListViewHolder> {    private final ArrayList<DataUser> listData;    public AdapterRows(ArrayList<DataUser> list){        this.listData = list;    }    @NonNull    @Override    public ListViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext()).inflate(R.layout.item_table, parent, false);        return new ListViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull ListViewHolder holder, int position) {        DataUser user = listData.get(position);        holder.nomor.setText(String.valueOf(position + 1));        holder.nama.setText(user.getNama());        holder.penerimaan.setText(user.getPenerimaan());        holder.produksi.setText(user.getProduksi());        holder.tanggal.setText(user.getTgl_pendaftaran());    }    @Override    public int getItemCount() {        return listData.size();    }    static class ListViewHolder extends RecyclerView.ViewHolder {        TextView nomor, nama, penerimaan, produksi, tanggal;        ListViewHolder(View itemView){            super(itemView);            nomor = itemView.findViewById(R.id.nomorItem);            nama = itemView.findViewById(R.id.namaPlatTable);            penerimaan = itemView.findViewById(R.id.penerimaanTable);            produksi = itemView.findViewById(R.id.sortTable);            tanggal = itemView.findViewById(R.id.produksiTable);        }    }}